
public class PrintAlphabets {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Capital Alphabets:");
		for (char ch = 'A'; ch <= 'Z'; ch++) {
			System.out.print(ch +" ");
		}
		
		System.out.println("\n\nSmall Alphabets:");
		for (char ch = 'a'; ch <= 'z'; ch++) {
			System.out.print(ch +" ");
		}
	}

}
